mapper.select:

	select count(*)
	from submit
	where userNo = #{userNo} and assignmentNo = #{assignmentNo}

mapper.update:

	update assignment
	set assignmentTitle = #{assignmentTitle},
		assignmentContent = #{assignmentContent},
		subjectNo = #{subjectNo},
		chapterNo = #{chapterNo}
	where assignmentNo = #{assignmentNo}

mapper.select:

select courseNo
from courseregist 
where userNo = #{userNo}
