mapper.select:
		
			select cpostTitle,regDate,liked, cpostNo
            from (
                select * 
                from communitypost 
                where cposttype = #{cposttype}
                order by liked desc
            )
            where  rownum <= 10


	SELECT locationNo
	FROM location
	where address = #{address}


	SELECT locationNo
	FROM locationPost
	WHERE cpostNo = #{cpostNo}


	SELECT businessName
	FROM location
	WHERE locationNo = #{locationNo}


	SELECT address
	FROM location
	WHERE locationNo = #{locationNo}


	SELECT latitude
	FROM location
	WHERE locationNo = #{locationNo}


	SELECT longitude
	FROM location
	WHERE locationNo = #{locationNo}

mapper.update:

	update COMMUNITYPOST 
	set liked = liked+1
	where cpostNo = #{cpostNo}


	update COMMUNITYPOST 
	set liked = liked-1
	where cpostNo = #{cpostNo}
