mapper.update:

	UPDATE 	 chapter 
	SET 	 chapterContent = #{chapterContent}
	WHERE 	 chapterNo = #{chapterNo}


	UPDATE 	 subject 
	SET 	 subjectTitle = #{subjectTitle}
	WHERE 	 subjectNo = #{subjectNo}


	UPDATE 	 schedule 
	SET 	 scheduleName = #{subjectTitle},
			 startDate = #{startDate},
			 endDate = TO_DATE(#{endDate}, 'yyyy/mm/dd hh24:mi:ss'),
			 scheduleMemo = #{scheduleMemo}
	WHERE 	 scheduleNo = #{scheduleNo}


	UPDATE 	 courseregist 
	SET 	 seatNo = #{seatNo}
	WHERE 	 userNo = #{userNo}
		AND	 courseNo = #{courseNo}


	UPDATE 	 courseregist 
	SET 	 accessStat = 1
	WHERE 	 userNo = #{userNo}
		AND	 courseNo = #{courseNo}


	UPDATE 	 courseregist 
	SET 	 accessStat = 0
	WHERE 	 userNo = #{userNo}
		AND	 courseNo = #{courseNo}


	UPDATE 	 feedbackanswer 
	SET 	 fbaType = 99
	WHERE 	 userNo = #{userNo}
		AND	 fbqNo = #{fbqNo}


	UPDATE 	 feedbackanswer 
	SET 	 fbaType = #{fbaType}
	WHERE 	 userNo = #{userNo}
		AND	 fbqNo = #{fbqNo}
