mapper.select:
select reply, postNo, userNo, regDate, replyContent, userName, logoPath from (
SELECT reply, postNo, r.userNo userNo, TO_CHAR(regDate, 'yyyy/mm/dd') as regDate, replyContent, u.userName userName, u.logoPath logoPath
FROM reply r join users u on r.userNo = u.userNo ) where postNo = #{postNo}

select reply, postNo, userNo, regDate, replyContent, userName, logoPath from (
SELECT reply, postNo, r.userNo userNo, TO_CHAR(regDate, 'yyyy/mm/dd') as regDate, replyContent, u.userName userName, u.logoPath logoPath
FROM reply r join users u on r.userNo = u.userNo ) where reply = #{reply}
